// External imports
import React, { useEffect, useMemo, useState } from "react";

// Internal imports
import Page from "../../components/nav/Page";
import Button from "../../components/buttons/Button";
import AuctionClock from "../../components/elements/AuctionClock";
import { useRootContext } from "../../contexts/RootContext";

type Item = {
	id: number;
	name: string;
	amount: number;
	startPrice: number;
	minimumPrice: number;
	duration: number;
};

type AuctionState = {
	items: Item[];
	currentIndex: number;
	timeLeft: number;
	countdown: number;
	running: boolean;
	resetToken: number;
	price: number;
	soldItems: Item[];
	completed?: boolean;
	lastUpdated: number;
};

const STORAGE_KEY = "auction_state";

function loadState(): AuctionState | null {
	const raw = typeof window !== "undefined" ? localStorage.getItem(STORAGE_KEY) : null;
	if (!raw) return null;
	try {
		return JSON.parse(raw);
	} catch {
		return null;
	}
}

function KoperDashboard() {
	const { t } = useRootContext();
	const [auction, setAuction] = useState<AuctionState | null>(() => loadState());
	const [qty, setQty] = useState<number>(5);
	const [price, setPrice] = useState<number>(auction?.price ?? 0);
	const [resetToken, setResetToken] = useState<number>(auction?.resetToken ?? 0);

	// Poll localStorage to reflect veilingmeester actions
	useEffect(() => {
		const id = setInterval(() => {
			const next = loadState();
			if (!next) return;
			setAuction(prev => {
				// bump reset token if changed
				if (next.resetToken !== prev?.resetToken) {
					setResetToken(next.resetToken);
					setPrice(next.price);
				}
				return next;
			});
			setPrice(next.price);
		}, 500);
		return () => clearInterval(id);
	}, []);

	const current = auction?.items[auction.currentIndex ?? 0];
	const currentStock = current?.amount ?? 0;

	useEffect(() => {
		setQty(q => Math.max(0, Math.min(currentStock, q)));
	}, [currentStock]);

	// clamp price display
	useEffect(() => {
		if (!auction || !current) return;
		setPrice(auction.price);
	}, [auction?.price, current?.id]);

	const upcoming = useMemo(() => {
		if (!auction) return [];
		const after = auction.items.slice((auction.currentIndex ?? 0) + 1);
		const before = auction.items.slice(0, auction.currentIndex ?? 0);
		return [...after, ...before];
	}, [auction]);

	return (
		<Page enableHeader className="user-dashboard">
			<section className="user-hero">
				<div className="user-hero-head">
					<div>
						<h1 className="user-hero-title">{t("koper_dashboard")}</h1>
						<p className="user-hero-sub">{t("koper_dashboard_sub")}</p>
					</div>
				</div>
			</section>

			<section className="user-card-wrap">
				<div className="user-card">
					<div className="user-card-mediaBlock">
						<div className="user-card-media bg-gray-100 flex items-center justify-center text-sm">
							{current ? current.name : "Geen product"}
						</div>
						<div className="product-info">
							<div className="prod-row">
								<span className="prod-label">{t("koper_product")}</span>
								<span className="prod-val prod-val--wide">{current?.name ?? "-"}</span>
								<span className="prod-label">{t("koper_country")}</span>
								<span className="prod-val">NL</span>
							</div>
							<div className="prod-row">
								<span className="prod-label">{t("koper_stems_per_bundle")}</span>
								<span className="prod-val prod-val--wide">{current?.amount ?? 0}</span>
							</div>
						</div>
					</div>

					<div className="user-card-center">
						<AuctionClock
							totalSeconds={current?.duration ?? 12}
							start={Boolean(auction?.running)}
							paused={(auction?.countdown ?? 0) > 0 || !auction?.running}
							resetToken={resetToken}
							round={1}
							coin={1}
							amountPerLot={current?.amount ?? 1}
							minAmount={1}
							price={price}
							onTick={secs => {
								if (!current) return;
								const pct = Math.max(0, secs) / (current.duration || 1);
								const next = Math.max(current.minimumPrice, current.startPrice * pct);
								setPrice(+next.toFixed(2));
							}}
							onComplete={() => {
								// no-op on koper side; veilingmeester handles auto-sell
							}}
						/>

						<div className="stock-text">
							{auction?.running
								? t("koper_stock", { count: currentStock })
								: "Wachten tot veilingmeester start"}
						</div>

						<div className="user-actions">
							<div className="buy-controls">
								<Button
									className="user-action-btn !bg-primary-main buy-full"
									label={`${t("koper_buy")} (${qty})`}
									onClick={() => {
										// placeholder: deduct local stock only for demo
										if (!auction || !current) return;
										const cur = currentStock;
										const delta = Math.min(qty, cur);
										const nextVal = Math.max(0, cur - delta);
										setAuction(prev => {
											if (!prev) return prev;
											const items = prev.items.map(i =>
												i.id === current.id ? { ...i, amount: nextVal } : i
											);
											return { ...prev, items };
										});
										setQty(Math.min(nextVal, qty));
									}}
									disabled={!auction?.running || (auction?.countdown ?? 0) > 0 || !current}
								/>
								<div className="buy-inline">
									<input
										type="number"
										min={0}
										max={currentStock}
										className="buy-inline-input"
										value={Number.isFinite(qty) ? qty : ""}
										onChange={e => {
											const val = parseInt(e.target.value, 10);
											if (Number.isNaN(val)) {
												setQty(0);
												return;
											}
											setQty(Math.max(0, Math.min(currentStock, val)));
										}}
										aria-label={t("koper_qty_input_aria")}
									/>
									<Button
										className="qty-max-btn btn-outline"
										label={t("koper_max_stock")}
										aria-label={t("koper_max_stock")}
										onClick={() => setQty(currentStock)}
										disabled={currentStock === 0}
									/>
								</div>
							</div>
						</div>
					</div>

					<aside className="upcoming-side">
						<h4 className="upcoming-side-title">{t("koper_upcoming")}</h4>
						<ul className="upcoming-side-list">
							{upcoming.map((p, i) => (
								<li className="upcoming-side-item" key={i}>
									<div className="upcoming-side-thumb bg-gray-200" />
									<div className="upcoming-side-info">
										<div className="upcoming-side-name">{p.name}</div>
										<div className="upcoming-side-meta">
											Start €{p.startPrice.toFixed(2)} • Min €{p.minimumPrice.toFixed(2)}
										</div>
									</div>
									<span className="upcoming-side-badge">{p.amount}</span>
								</li>
							))}
						</ul>
					</aside>
				</div>
			</section>
		</Page>
	);
}

export default KoperDashboard;
