import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import Button from '../../buttons/Button';
import FormInputField from '../../elements/FormInputField';
import { PassThrough } from "stream";

type UserType = "koper" | "kweker" | "veilingmeester";

interface InputField {
  label: string;
  type: string;
  placeholder?: string;
  options?: string[];
}

function RegisterContent() {
  const navigate = useNavigate();
  const [userType, setUserType] = useState<UserType>("koper");
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState<Record<string, string>>({});
  const handleGoBack = () => navigate('/');

  // ... (rest of your component logic remains the same)
  // ... (regions array, steps object, totalSteps, currentFields, handleInputChange are unchanged)

  const regions = [
    "Noord-Holland",
    "Zuid-Holland",
    "Utrecht",
    "Gelderland",
    "Overijssel",
    "Limburg",
    "Friesland",
    "Drenthe",
    "Flevoland",
    "Groningen",
    "Zeeland",
  ];

  const t = window.application.t;
  const steps: Record<UserType, InputField[][]> = {
    koper: [
      [
      { label: t('email'), type: "email", placeholder: "you@example.com" },
      { label: t('password'), type: "password", placeholder: "â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢" },
      { label: t('first_name'), type: "text", placeholder: "Steve" },
      { label: t('last_name'), type: "text", placeholder: "Jobs" },
    ],
      [
        { label: t('address'), type: "text", placeholder: "Street 123" },
        { label: t('postcode'), type: "text", placeholder: "1234 AB" },
        { label: t('region'), type: "select", options: regions },
      ],
    ],
    kweker: [
      [
        { label: t('company_name'), type: "text", placeholder: "Example BV" },
        { label: t('email'), type: "email", placeholder: "you@example.com" },
        { label: t('password'), type: "password", placeholder: "â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢" },
        { label: t('kvk_number'), type: "text", placeholder: "12345678" },
      ],
      [
        {label: t('phonenumber'), type: "text", placeholder: "+31 6 12345678"},
        { label: t('address'), type: "text", placeholder: "Street 123" },
        { label: t('postcode'), type: "text", placeholder: "1234 AB" },
        { label: t('region'), type: "select", options: regions },
      ],
    ],
    veilingmeester: [
      [
        { label: t('email'), type: "email", placeholder: "you@example.com" },
        { label: t('password'), type: "password", placeholder: "â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢" },
      ],
      [
        { label: t('region'), type: "select", options: regions },
        { label: t('authorisation_code'), type: "text", placeholder: "123456" },
      ],
    ],
  };

  const totalSteps = steps[userType].length;
  const currentFields = steps[userType][step - 1];

  const handleInputChange = (key: string, value: string) => {
    setFormData((prev) => ({ ...prev, [key]: value }));
  };

  //
  // ðŸ‘‡ MODIFIED handleSubmit FUNCTION
  //
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // 1. Log the final data
    console.log("Final submitted data:", formData);

    // 2. Ideally, send the data to a server for account creation...
    //    (You would put your API call here)

    // 3. Define the destination route based on the selected userType
    let destination = "/"; // Fallback to homepage

    switch (userType) {
      case "koper":
        destination = "/user-dashboard";
        break;
      case "kweker":
        destination = "/kweker";
        // Replace with actual kweker dashboard route
        break;
      case "veilingmeester":
        destination = "/dashboard"; // Replace with actual veilingmeester dashboard route
        break;
      default:
        break;
    }

    // 4. Show success message (optional, replace with proper notification/state management)
    alert(`Account created successfully as ${userType}! Redirecting...`);

    // 5. Navigate to the appropriate dashboard
    navigate(destination);
  };
  //
  // ðŸ‘† END MODIFIED handleSubmit FUNCTION
  //

  return (
    <div className="app-page register-page">
      <div className="register-card">
        <Button 
            className="logo-icon" 
            icon="/svg/logo-flori.svg"
            onClick={handleGoBack}
            aria-label={window.application.t('back_button_aria')}
        />

        <div className="register-header">
          <h2 className="register-title"> {window.application.t('create_account')}</h2>
          <p className="register-subtitle" aria-live="polite">
            {window.application.t('step')} {step} {window.application.t('of')} {totalSteps}
          </p>
        </div>

        <div
          className="progress-bar"
          role="progressbar"
          aria-valuenow={step}
          aria-valuemin={1}
          aria-valuemax={totalSteps}
          aria-label={`Registration progress: step ${step} of ${totalSteps}`}
        >
          <div
            className="progress-bar-fill"
            style={{ width: `${(step / totalSteps) * 100}%` }}
          ></div>
        </div>

        <div
          className="register-tabs"
          role="tablist"
          aria-label="Select user type"
        >
          {(["koper", "kweker", "veilingmeester"] as UserType[]).map((type) => (
            <div
              key={type}
              className={`register-tab ${userType === type ? "active" : ""}`}
              onClick={() => {
                setUserType(type);
                setStep(1);
              }}
              role="tab"
              aria-selected={userType === type}
              tabIndex={0}
              aria-label={`Register as ${type}`}
            >
              {type.charAt(0).toUpperCase() + type.slice(1)}
            </div>
          ))}
        </div>

        <form className="register-form" onSubmit={handleSubmit}>
          {currentFields.map((field, idx) => {
            const key = `${userType}-${field.label}`;
            const ariaLabel = `${field.label} for ${userType}`;

            if (field.type === "select") {
              return (
                <div key={idx}>
                <FormInputField id='email' label={key} className='input-field' error='Please type an email (example@email.com)' isError={false} />

                  <label htmlFor={key}>{field.label}</label>
                  <select
                    id={key}
                    className="input-field"
                    aria-label={ariaLabel}
                    aria-required="true"
                    value={formData[key] || ""}
                    onChange={(e) => handleInputChange(key, e.target.value)}
                  >
                    <option value="" disabled>
                      Select {field.label.toLowerCase()}
                    </option>
                    {field.options!.map((option) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </div>
              );
            }

            return (
              <div key={idx}>
                <label htmlFor={key}>{field.label}</label>
                <input
                  id={key}
                  type={field.type}
                  placeholder={field.placeholder}
                  className="input-field"
                  value={formData[key] || ""}
                  onChange={(e) => handleInputChange(key, e.target.value)}
                  aria-label={ariaLabel}
                  aria-required="true"
                />
              </div>
            );
          })}

          <div className="form-buttons">
            {step > 1 && (
              <button
                type="button"
                className="btn-secondary"
                onClick={(e) => {
                  e.preventDefault();
                  setStep(step - 1);
                }}
                aria-label="Go back to previous step"
              >
                Back
              </button>
            )}

            {step < totalSteps ? (
              <button
                type="button"
                className="btn-primary"
                onClick={(e) => {
                  e.preventDefault();
                  setStep(step + 1);
                }}
                aria-label="Go to next step"
              >
                Next
              </button>
            ) : (
              <button
                type="submit"
                className="btn-primary"
                aria-label="Submit registration form and create account"
              >
                Create Account
              </button>
            )}
          </div>

          <button
            type="button"
            className="login-link"
            onClick={() => navigate("/login")}
            aria-label="Go to login page instead"
          >
            Login instead?
          </button>
        </form>
      </div>
    </div>
  );
}

export default RegisterContent;
