// External imports
import React from 'react';
import clsx from "clsx";

// Internal imports
import {joinClsx} from "../../utils/classPrefixer";

// 1. Revert to extending ButtonHTMLAttributes<HTMLButtonElement>
type FormLinkProps = {
    labelClassName?: string;
    label: string; 
    // icon prop was referenced in original code, so adding it for completeness
    icon?: string; 
} & React.ButtonHTMLAttributes<HTMLButtonElement>; 

function FormLink(props: FormLinkProps) {
    // 2. Remove the 'href' prop (it's for links) and ensure 'icon' is destructured
    const { label, className, labelClassName, icon, ...rest } = props; 
    
    // 3. Revert the return element to a <button>
    // This allows the component to properly handle the onClick={() => navigate(...)}
    return <button 
        className={clsx('base-btn-text-link', className)} // These classes likely make it look like a link
        type="button" // Good practice to explicitly set type for non-submit buttons
        {...rest}
    >
        {/* NOTE: I kept the original icon logic, assuming 'icon' is now defined 
            and either a string for a class (like 'bi-arrow-right') or an image URL.
        */}
        {icon && <i className={clsx("base-btn-icon", joinClsx(className, 'icon'), "bi", icon)}/>}
        {icon && !icon.startsWith('bi-') && <img src={icon} className="base-btn-icon" alt="" />}
        
        {/* 4. This is the part that renders your visible text */}
        {label && <span className={clsx('base-btn-txt', joinClsx(className, 'txt'), labelClassName)}>{label}</span>}
        
    </button>;
}

export default FormLink;
