import React, { useState, useEffect } from "react";
import Page from "../../components/nav/Page";

function VeilingMeesterDashboard() {
    const [clock, setClock] = useState(null);
    const [items, setItems] = useState([]);
    const [currentIndex, setCurrentIndex] = useState(0);
    const [timeLeft, setTimeLeft] = useState(0);
    const [auctionRunning, setAuctionRunning] = useState(false);

    // 1. Load active veilingklok
    useEffect(() => {
        async function loadClock() {
            const res = await fetch("/api/veilingklok/all?activeOnly=true");
            const data = await res.json();

            const activeClock = data.veilingKlokken?.[0];
            if (activeClock) {
                setClock(activeClock);
                setTimeLeft(activeClock.durationInSeconds);
            }
        }

        loadClock();
    }, []);

    // 2. Load products for this veilingklok
    useEffect(() => {
        if (!clock) return;

        async function loadProducts() {
            const res = await fetch(`/api/product/byClock/${clock.id}`);
            const products = await res.json();
            setItems(products);
        }

        loadProducts();
    }, [clock]);

    const currentItem = items[currentIndex];

    // 3. Timer logic
    useEffect(() => {
        if (!auctionRunning || !currentItem) return;

        if (timeLeft <= 0) {
            finishProduct();
            return;
        }

        const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
        return () => clearTimeout(timer);
    }, [auctionRunning, timeLeft, currentItem]);

    // 4. Finish product → update backend → switch to next
    async function finishProduct() {
        await fetch(`/api/product/${currentItem.id}/markSold`, { method: "PATCH" });

        setCurrentIndex(prev => prev + 1);
        setTimeLeft(clock.durationInSeconds);
        setAuctionRunning(false);
    }

    return (
        <Page enableHeader enableFooter className="veilingmeester-page">
            <main className="veilingmeester-container">

                <h1>Hallo, veilingmeester!</h1>
                <p>Welkom op de dashboard pagina! Hier kan je de veiling starten en bekijken.</p>

                {currentItem && (
                    <section className="active-auction-card">
                        <div className="active-auction-header">
                            <h2 className="active-auction-title">Huidige veiling</h2>
                        </div>

                        <div className="active-auction-body">
                            <div className="active-auction-image"></div>

                            <div className="active-auction-info">
                                <h3>{currentItem.name}</h3>
                                <p>Aantal: {currentItem.amount}</p>
                                <p>Prijs: €{currentItem.price}</p>

                                <p className="auction-time">Tijd resterend: {timeLeft}s</p>
                            </div>

                            <button
                                onClick={() => setAuctionRunning(!auctionRunning)}
                                className="auction-start-btn"
                            >
                                {auctionRunning ? "Stop" : "Start"}
                            </button>
                        </div>
                    </section>
                )}

                {/* Upcoming items */}
                <section className="product-list-card">
                    <h2 className="product-list-header">Komende producten</h2>

                    <div className="product-list-items">
                        {items.slice(currentIndex + 1).map(item => (
                            <div key={item.id} className="product-item">
                                <div className="product-item-image"></div>

                                <div className="product-item-info">
                                    <h3>{item.name}</h3>
                                    <p>Aantal: {item.amount}</p>
                                    <p>Prijs: €{item.price}</p>
                                </div>

                                <button className="product-start-btn" disabled>
                                    Wachten
                                </button>
                            </div>
                        ))}
                    </div>
                </section>
            </main>
        </Page>
    );
}

export default VeilingMeesterDashboard;
